// src/Images.jsx
import React, { useState, useRef } from "react";
import "./App.css";

function Resources() {
  const [file, setFile] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [message, setMessage] = useState("");
  const [isDragging, setIsDragging] = useState(false);
  const [uploadDone, setUploadDone] = useState(false);
  const [deleteInput, setDeleteInput] = useState("");
  const [deleteMessage, setDeleteMessage] = useState("");
  const [textInput, setTextInput] = useState(""); // ðŸ†• added text input state
  const fileInputRef = useRef(null);

  const allowedFiles = [
    "banner1.jpg",
    "banner2.jpg",
    "banner3.jpg",
    "about.jpg",
    "nia.jpg",
    "sarah.jpg",
    "mara.jpg",
    "hillary.jpg",
    "radha.jpg",
    "christian.jpg",
    "ameen.jpg",
  ];

  const isValidFile = (file) => {
    const validType =
      file.type === "image/jpeg" || file.name.toLowerCase().endsWith(".jpg");
    const validName = allowedFiles.includes(file.name.toLowerCase());
    return validType && validName;
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (isValidFile(selectedFile)) {
        console.log("File selected via input:", selectedFile.name);
        setFile(selectedFile);
        setMessage("");
      } else {
        setMessage("Invalid file. Please select one of the allowed .jpg files.");
        setFile(null);
        if (fileInputRef.current) fileInputRef.current.value = "";
      }
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setMessage("Please select or drop a valid .jpg file first.");
      return;
    }

    if (!textInput.trim()) {
      setMessage("Please enter some text before uploading.");
      return;
    }

    // ðŸ†• Create a text file from user input
    const textBlob = new Blob([textInput], { type: "text/plain" });
    const textFileName = file.name.replace(".jpg", ".txt");
    const textFile = new File([textBlob], textFileName, { type: "text/plain" });

    const formData = new FormData();
    formData.append("file", file);
    formData.append("textfile", textFile); // ðŸ†• also upload text file

    try {
      const response = await fetch(
        "https://sadseditor-production.up.railway.app/upload",
        {
          method: "POST",
          body: formData,
        }
      );

      const data = await response.json();

      if (response.ok) {
        setMessage("Upload successful!");
        setImageUrl(
          `https://sadseditor-production.up.railway.app/uploads/${data.filename}`
        );
        setUploadDone(true);
        setDeleteInput(file.name);
        setFile(null);
        setTextInput(""); // ðŸ†• clear text input
        if (fileInputRef.current) fileInputRef.current.value = "";
      } else {
        setMessage(`Upload failed: ${data.message || "Server error"}`);
      }
    } catch (error) {
      console.error("Upload error:", error.message);
      setMessage(`Upload failed: ${error.message}`);
    }
  };

  return (
    <div
      style={{
        backgroundColor: "#252525",
        color: "white",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        fontFamily: '"Riona Sans W01 Medium", sans-serif',
        fontSize: "1rem",
      }}
    >
      <img
        src="https://sads.club/static/media/sads_logo.88d07e896311a6f9aa80.png"
        alt="SADS Logo"
        style={{
          height: "125px",
          display: "block",
          margin: "0 auto 15px auto",
        }}
      />

      {/* ðŸ†• Text input field */}
      <textarea
        placeholder="Write text for your file..."
        value={textInput}
        onChange={(e) => setTextInput(e.target.value)}
        style={{
          width: "100%",
          maxWidth: "400px",
          height: "100px",
          marginBottom: "15px",
          backgroundColor: "#404040",
          color: "white",
          padding: "10px",
          border: "none",
          resize: "none",
          outline: "none",
        }}
      />

      {/* Existing file upload section */}
      <div
        onDragEnter={(e) => {
          e.preventDefault();
          e.stopPropagation();
          setIsDragging(true);
        }}
        onDragLeave={(e) => {
          e.preventDefault();
          e.stopPropagation();
          setIsDragging(false);
        }}
        onDragOver={(e) => {
          e.preventDefault();
          e.stopPropagation();
          e.dataTransfer.dropEffect = "copy";
        }}
        onDrop={(e) => {
          e.preventDefault();
          e.stopPropagation();
          setIsDragging(false);
          const droppedFile = e.dataTransfer.files[0];
          if (droppedFile) {
            if (isValidFile(droppedFile)) {
              console.log("File dropped:", droppedFile.name);
              setFile(droppedFile);
              setMessage("");
            } else {
              setMessage(
                "Invalid file. Please drop one of the allowed .jpg files."
              );
              setFile(null);
            }
          }
        }}
        style={{
          backgroundColor: isDragging ? "#505050" : "#404040",
          padding: "15px",
          width: "100%",
          maxWidth: "400px",
          textAlign: "center",
        }}
      >
        <input
          type="file"
          accept="image/jpeg,.jpg"
          onChange={handleFileChange}
          ref={fileInputRef}
          style={{
            color: "white",
            width: "100%",
            marginBottom: "10px",
          }}
        />
        <button
          onClick={handleUpload}
          style={{
            backgroundColor: "#606060",
            color: "white",
            padding: "8px 16px",
            border: "none",
            cursor: "pointer",
            fontSize: "1rem",
            filter: "brightness(100%)",
          }}
          onMouseEnter={(e) => (e.target.style.filter = "brightness(85%)")}
          onMouseLeave={(e) => (e.target.style.filter = "brightness(100%)")}
        >
          Upload
        </button>
      </div>

      {!uploadDone && (
        <div
          style={{
            marginTop: "0px",
            textAlign: "center",
            color: "#aaa",
            fontSize: "0.9rem",
            maxWidth: "400px",
          }}
        >
          <p>Allowed file names:</p>
          <p style={{ whiteSpace: "pre-line", margin: 0 }}>
            {allowedFiles.join("\n")}
          </p>
        </div>
      )}
      {message && <p style={{ marginTop: "15px" }}>{message}</p>}
      {imageUrl && (
        <div style={{ marginTop: "0px", textAlign: "center" }}>
          <p>Uploaded image:</p>
          <a href={imageUrl} target="_blank" rel="noopener noreferrer">
            {imageUrl}
          </a>
          <div>
            <img
              src={imageUrl}
              alt="Uploaded"
              style={{
                width: "300px",
                marginTop: "10px",
                display: "block",
                margin: "0 auto",
              }}
            />
          </div>
        </div>
      )}
      <div
        style={{
          marginTop: "20px",
          backgroundColor: "#404040",
          padding: "15px",
          width: "100%",
          maxWidth: "400px",
          textAlign: "center",
        }}
      >
        <form onSubmit={handleDelete}>
          <input
            type="text"
            placeholder="Type file name to delete (e.g. banner1.jpg)"
            value={deleteInput}
            onChange={(e) => setDeleteInput(e.target.value)}
            style={{
              width: "90%",
              padding: "8px",
              marginBottom: "10px",
              border: "none",
              outline: "none",
              backgroundColor: "#555",
              color: "white",
              textAlign: "center",
            }}
          />
          <button
            type="submit"
            style={{
              backgroundColor: "#c0392b",
              color: "white",
              padding: "8px 16px",
              border: "none",
              cursor: "pointer",
              fontSize: "1rem",
            }}
          >
            Delete
          </button>
        </form>
        {deleteMessage && <p style={{ marginTop: "10px" }}>{deleteMessage}</p>}
      </div>
    </div>
  );
}

export default Resources;
