import React, { useState, useRef } from "react";
import "./App.css";

function Resources() {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState("");
  const [isDragging, setIsDragging] = useState(false);
  const [uploadDone, setUploadDone] = useState(false);
  const [textInput, setTextInput] = useState("");
  const [uploadedFiles, setUploadedFiles] = useState({ textfile: "", image: "" });
  const fileInputRef = useRef(null);

  const isValidFile = (file) =>
    file.type === "image/jpeg" || file.name.toLowerCase().endsWith(".jpg");

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (isValidFile(selectedFile)) {
        setFile(selectedFile);
        setMessage("");
      } else {
        setMessage("Invalid file. Only .jpg allowed.");
        setFile(null);
        if (fileInputRef.current) fileInputRef.current.value = "";
      }
    }
  };

  const handleUpload = async () => {
    if (!textInput.trim()) {
      setMessage("Please enter some text to upload.");
      return;
    }
    if (!file) {
      setMessage("Please select an image to upload.");
      return;
    }

    // Create text file
    const textBlob = new Blob([textInput], { type: "text/plain" });
    const textFile = new File([textBlob], "textfile.txt", { type: "text/plain" });

    const formData = new FormData();
    formData.append("textfile", textFile);
    formData.append("file", file); // image is required

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const text = await response.text();
      let data = {};
      try {
        data = JSON.parse(text);
      } catch {
        data = { message: text };
      }

      if (response.ok) {
        setMessage("Upload successful!");
        setUploadDone(true);
        setUploadedFiles({ textfile: data.textfile, image: data.image });
        setTextInput("");
        setFile(null);
        if (fileInputRef.current) fileInputRef.current.value = "";
      } else {
        setMessage(`Upload failed: ${data.message || "Server error"}`);
      }
    } catch (error) {
      setMessage(`Upload failed: ${error.message}`);
    }
  };

  return (
    <div
      style={{
        backgroundColor: "#252525",
        color: "white",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        fontFamily: '"Riona Sans W01 Medium", sans-serif',
        fontSize: "1rem",
      }}
    >
      <img
        src="https://sads.club/static/media/sads_logo.88d07e896311a6f9aa80.png"
        alt="SADS Logo"
        style={{ height: "125px", display: "block", margin: "0 auto 15px auto" }}
      />

      <textarea
        placeholder="Write text here..."
        value={textInput}
        onChange={(e) => setTextInput(e.target.value)}
        style={{
          width: "100%",
          maxWidth: "400px",
          height: "36px",
          marginBottom: "15px",
          backgroundColor: "#404040",
          color: "white",
          padding: "10px",
          border: "none",
          resize: "none",
          outline: "none",
          fontSize: "15px",
        }}
      />

      <div
        onDragEnter={(e) => { e.preventDefault(); e.stopPropagation(); setIsDragging(true); }}
        onDragLeave={(e) => { e.preventDefault(); e.stopPropagation(); setIsDragging(false); }}
        onDragOver={(e) => { e.preventDefault(); e.stopPropagation(); e.dataTransfer.dropEffect = "copy"; }}
        onDrop={(e) => {
          e.preventDefault();
          e.stopPropagation();
          setIsDragging(false);
          const droppedFile = e.dataTransfer.files[0];
          if (droppedFile) {
            if (isValidFile(droppedFile)) {
              setFile(droppedFile);
              setMessage("");
            } else {
              setMessage("Invalid file. Only .jpg allowed.");
              setFile(null);
            }
          }
        }}
        style={{
          backgroundColor: isDragging ? "#505050" : "#404040",
          padding: "15px",
          width: "100%",
          maxWidth: "400px",
          textAlign: "center",
          marginBottom: "15px",
        }}
      >
        <input
          type="file"
          accept="image/jpeg,.jpg"
          onChange={handleFileChange}
          ref={fileInputRef}
          style={{ color: "white", width: "100%", marginBottom: "10px" }}
        />
        <button
          onClick={handleUpload}
          style={{
            backgroundColor: "#606060",
            color: "white",
            padding: "8px 16px",
            border: "none",
            cursor: "pointer",
            fontSize: "1rem",
          }}
        >
          Upload
        </button>
      </div>

      {message && <p style={{ marginTop: "15px" }}>{message}</p>}

      {uploadDone && (
        <div style={{ marginTop: "15px", textAlign: "center" }}>
          <p>Uploaded text file: {uploadedFiles.textfile}</p>
          <p>Uploaded image file: {uploadedFiles.image}</p>
          <div>
            <img
              src={`http://localhost:5000/uploads/${uploadedFiles.image}`}
              alt="Uploaded"
              style={{ width: "300px", marginTop: "10px" }}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default Resources;
