// src/Images.jsx
import React, { useState, useRef } from "react";
import "./App.css";

function Resources() {
  const [file, setFile] = useState(null); // optional image
  const [message, setMessage] = useState("");
  const [isDragging, setIsDragging] = useState(false);
  const [uploadDone, setUploadDone] = useState(false);
  const [textInput, setTextInput] = useState(""); // required text
  const fileInputRef = useRef(null);

  const isValidFile = (file) => {
    return file.type === "image/jpeg" || file.name.toLowerCase().endsWith(".jpg");
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (isValidFile(selectedFile)) {
        setFile(selectedFile);
        setMessage("");
      } else {
        setMessage("Invalid file. Please select a .jpg file.");
        setFile(null);
        if (fileInputRef.current) fileInputRef.current.value = "";
      }
    }
  };

  const handleUpload = async () => {
    if (!textInput.trim()) {
      setMessage("Please enter some text before uploading.");
      return;
    }

    // Create a text file from user input
    const textBlob = new Blob([textInput], { type: "text/plain" });
    const textFileName = (file ? file.name.replace(".jpg", ".txt") : "textfile.txt");
    const textFile = new File([textBlob], textFileName, { type: "text/plain" });

    const formData = new FormData();
    formData.append("textfile", textFile); // always send text file
    if (file) formData.append("file", file); // optional image

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      // handle JSON or text safely
      let data = {};
      try {
        data = await response.json();
      } catch {
        const text = await response.text();
        data = { message: text };
      }

      if (response.ok) {
        setMessage("Upload successful!");
        setUploadDone(true);
        setTextInput("");
        setFile(null);
        if (fileInputRef.current) fileInputRef.current.value = "";
      } else {
        setMessage(`Upload failed: ${data.message || "Server error"}`);
      }
    } catch (error) {
      console.error("Upload error:", error.message);
      setMessage(`Upload failed: ${error.message}`);
    }
  };

  return (
    <div
      style={{
        backgroundColor: "#252525",
        color: "white",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        fontFamily: '"Riona Sans W01 Medium", sans-serif',
        fontSize: "1rem",
      }}
    >
      <img
        src="https://sads.club/static/media/sads_logo.88d07e896311a6f9aa80.png"
        alt="SADS Logo"
        style={{ height: "125px", marginBottom: "15px" }}
      />

      {/* Text input field */}
      <textarea
        placeholder="Write title here..."
        value={textInput}
        onChange={(e) => setTextInput(e.target.value)}
        style={{
          width: "100%",
          maxWidth: "400px",
          height: "36px",
          marginBottom: "15px",
          backgroundColor: "#404040",
          color: "white",
          padding: "10px",
          border: "none",
          resize: "none",
          outline: "none",
          fontSize: "15px",
        }}
      />

      {/* Optional image upload */}
      <div
        onDragEnter={(e) => {
          e.preventDefault();
          e.stopPropagation();
          setIsDragging(true);
        }}
        onDragLeave={(e) => {
          e.preventDefault();
          e.stopPropagation();
          setIsDragging(false);
        }}
        onDragOver={(e) => {
          e.preventDefault();
          e.stopPropagation();
          e.dataTransfer.dropEffect = "copy";
        }}
        onDrop={(e) => {
          e.preventDefault();
          e.stopPropagation();
          setIsDragging(false);
          const droppedFile = e.dataTransfer.files[0];
          if (droppedFile && isValidFile(droppedFile)) {
            setFile(droppedFile);
            setMessage("");
          } else if (droppedFile) {
            setMessage("Invalid file. Only .jpg allowed.");
            setFile(null);
          }
        }}
        style={{
          backgroundColor: isDragging ? "#505050" : "#404040",
          padding: "15px",
          width: "100%",
          maxWidth: "400px",
          textAlign: "center",
          marginBottom: "15px",
        }}
      >
        <input
          type="file"
          accept="image/jpeg,.jpg"
          onChange={handleFileChange}
          ref={fileInputRef}
          style={{ color: "white", width: "100%", marginBottom: "10px" }}
        />
        <button
          onClick={handleUpload}
          style={{
            backgroundColor: "#606060",
            color: "white",
            padding: "8px 16px",
            border: "none",
            cursor: "pointer",
            fontSize: "1rem",
          }}
        >
          Upload
        </button>
      </div>

      {message && <p style={{ marginTop: "15px" }}>{message}</p>}
    </div>
  );
}

export default Resources;
