import React, { useState, useRef } from "react";
import "./App.css";

function Resources() {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState("");
  const [isDragging, setIsDragging] = useState(false);
  const [uploadDone, setUploadDone] = useState(false);
  const [textInput, setTextInput] = useState("");
  const [uploadedFiles, setUploadedFiles] = useState({ textfile: "", image: "" });
  const fileInputRef = useRef(null);

  const isValidFile = (file) =>
    file.type === "image/jpeg" || file.name.toLowerCase().endsWith(".jpg");

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (isValidFile(selectedFile)) {
        setFile(selectedFile);
        setMessage("");
      } else {
        setMessage("Invalid file. Only .jpg allowed.");
        setFile(null);
        if (fileInputRef.current) fileInputRef.current.value = "";
      }
    }
  };

  const handleUpload = async () => {
    if (!textInput.trim()) {
      setMessage("Please enter a title to upload.");
      return;
    }
    if (!file) {
      setMessage("Please select an image to upload.");
      return;
    }

    const textBlob = new Blob([textInput], { type: "text/plain" });
    const textFile = new File([textBlob], "title.txt", { type: "text/plain" });

    const formData = new FormData();
    formData.append("textfile", textFile);
    formData.append("file", file);

    try {
      console.log("Uploading files:", { textFile: textFile.name, imageFile: file.name });
      for (let [key, value] of formData.entries()) {
        console.log(`FormData: ${key} = ${value.name}`);
      }

      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      console.log("Server response:", data);

      if (response.ok) {
        setMessage("Upload successful!");
        setUploadDone(true);
        setUploadedFiles({ textfile: data.textfile, image: data.image });
        setTextInput("");
        setFile(null);
        if (fileInputRef.current) fileInputRef.current.value = "";
      } else {
        setMessage(`Upload failed: ${data.message || "Server error"}`);
      }
    } catch (error) {
      console.error("Upload error:", error);
      setMessage(`Upload failed: ${error.message}`);
    }
  };

  return (
    <div
      style={{
        backgroundColor: "#252525",
        color: "white",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        fontFamily: '"Riona Sans W01 Medium", sans-serif',
        fontSize: "1rem",
      }}
    >
      <img
        src="https://sads.club/static/media/sads_logo.88d07e896311a6f9aa80.png"
        alt="SADS Logo"
        style={{ height: "125px", display: "block", margin: "0 auto 15px auto" }}
      />

      <div
        style={{
          marginTop: "20px",
          backgroundColor: "#404040",
          padding: "15px",
          width: "100%",
          maxWidth: "400px",
          textAlign: "center",
        }}
      >
        <textarea
          placeholder="Write title here..."
          value={textInput}
          onChange={(e) => setTextInput(e.target.value)}
          style={{
            width: "90%",
            padding: "8px",
            marginBottom: "10px",
            border: "none",
            outline: "none",
            backgroundColor: "#555",
            color: "white",
            textAlign: "left",
            resize: "none",
            fontSize: "15px",
          }}
        />
        <input
          type="file"
          accept="image/jpeg,.jpg"
          onChange={handleFileChange}
          ref={fileInputRef}
          style={{
            width: "90%",
            padding: "8px",
            marginBottom: "10px",
            border: "none",
            outline: "none",
            backgroundColor: "#404040",
            color: "white",
            textAlign: "left",
          }}
        />
        <button
          onClick={handleUpload}
          style={{
            backgroundColor: "#c0392b",
            color: "white",
            padding: "8px 16px",
            border: "none",
            cursor: "pointer",
            fontSize: "1rem",
          }}
        >
          Upload
        </button>
      </div>

      {message && <p style={{ marginTop: "10px", marginBottom: "0px" }}>{message}</p>}

      {uploadDone && (
        <div style={{ marginTop: "15px", textAlign: "center" }}>
          <p>
            Uploaded text file:{" "}
            <a
              href={`http://localhost:5000/uploads/${uploadedFiles.textfile}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {uploadedFiles.textfile}
            </a>
          </p>
          <p>
            Uploaded image file:{" "}
            <a
              href={`http://localhost:5000/uploads/${uploadedFiles.image}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {uploadedFiles.image}
            </a>
          </p>
          <div>
            <img
              src={`http://localhost:5000/uploads/${uploadedFiles.image}`}
              alt="Uploaded"
              style={{ width: "300px", marginTop: "10px" }}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default Resources;
